import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Data = props => (
  <tr>
    <td>{props.data.username}</td>
    <td>{props.data.age}</td>
    <td>{props.age.location}</td>
    <td>{props.exercise.date.substring(0,10)}</td>
    <td>
      <Link to={"/edit/"+props.data._id}>edit</Link> | <a href="#" onClick={() => { props.deleteData(props.data._id) }}>delete</a>
    </td>
  </tr>
)

export default class Home extends Component {
  constructor(props) {
    super(props);

    this.deleteData = this.deleteData.bind(this)

    this.state = {datas: []};
  }

  componentDidMount() {
    axios.get('http://localhost:5000/data/')
      .then(response => {
        this.setState({ exercises: response.data })
      })
      .catch((error) => {
        console.log(error);
      })
  }

  deleteData(id) {
    axios.delete('http://localhost:5000/data/'+id)
      .then(response => { console.log(response.data)});

    this.setState({
      datas: this.state.data.filter(el => el._id !== id)
    })
  }

  dataList() {
    return this.state.datas.map(currentdata => {
      return <Data data={currentdata} deleteData={this.deleteData} key={currentdata._id}/>;
    })
  }

  render() {
    return (
      <div>
        <h3>Logged Exercises</h3>
        <table className="table">
          <thead className="thead-light">
            <tr>
              <th>Username</th>
              <th>Age</th>
              <th>Location</th>
              <th>Date</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            { this.dataList() }
          </tbody>
        </table>
      </div>
    )
  }
}
