import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';


const Data = props => (
    <tr>
        <td>{props.data.img}</td>
      <td>{props.data.username}</td>
      <td>{props.data.age}</td>
      <td>{props.data.location}</td>
      <td>{props.data.date.substring(0,10)}</td>
      
      <td>
        <Link to={"/edit/"+props.data._id}>edit</Link> | <a href="#" onClick={() => { props.deleteExercise(props.data._id) }}>delete</a>
      </td>
    </tr>
  )
  
  export default class Home extends Component {
    constructor(props) {
      super(props);
  
      this.deleteData = this.deleteData.bind(this)
  
      this.state = {datas: []};
    }
  
    componentDidMount() {
      axios.get('http://localhost:5000/datas/')
        .then(response => {
          this.setState({ datas: response.data })
        })
        .catch((error) => {
          console.log(error);
        })
    }
  
    deleteData(id) {
      axios.delete('http://localhost:5000/datas/'+id)
        .then(response => { console.log(response.data)});
  
      this.setState({
        datas: this.state.datas.filter(el => el._id !== id)
      })
    }
  
    dataList() {
      return this.state.datas.map(currentdata=> {
        return <Data datas={urrentdata} deleteData={this.deleteData} key={currentdata._id}/>;
      })
    }
  
    render() {
      return (
        <div>
          <h3>Missing People List</h3>
          <table className="table">
            <thead className="thead-light">
              <tr>
                  <th>Pic</th>
                <th>Username</th>
                <th>Age</th>
                <th>location</th>
                <th>Date</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              { this.dataList() }
            </tbody>
          </table>
        </div>
      )
    }
  }
