import React, { Component  , useState} from 'react';
import axios from 'axios';
import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";
import {History, useHistory} from "react-router-dom";
import { Container } from 'react-bootstrap';

const CreateData = () =>  {

  const history= useHistory();
  const[user,setUser]=useState({
     username:"",age:"",location:"",date:"",img:""
      });

      let username ,value;
      const Handleinput = (e)=>{
         console.log(e);
         username=e.target.name;
         value=e.target.value;
 
         setUser({...user,[username]:value})
      }
      const fileinput =(e)=>{
        setUser(e.target.files[0]);
      }
      const postdata= async (e)=>
      {
        e.preventDefault();
        const{username,age,location,date,img} =user;
 
         const res=await fetch('/add',{
            method:"POST",
            headers:{
               "Content-Type":"application/json"
            },
            body:JSON.stringify({
               username,age,location,date,img
            })
 
         });
         const data =await res.json();
         if(data.status===422||!data)
         {
             window.alert("invalid")
         }
         else{
            window.alert("registration succesufull");
            console.log("success");
 
            history.push("/");
         }
      } 
   

    return (
    <Container>
      <h3>Create New Exercise Log</h3>
      <form method="POST" encType="multipart/form-data">
        <div className="form-group"> 
          <label>Username: </label>
          <input  type="text"
              required
              name="username"
              className="form-control"
              value={user.username}
              onChange={Handleinput}
              />
        </div>
        <div className="form-group"> 
          <label>Image</label>
          <input  type="file" method="POST"
              required
              className="form-control"
             
              filename="img"
              value={user.img}
               onChange={fileinput}
              />
        </div>
        <div className="form-group"> 
          <label>Age </label>
          <input  type="text"
              required
              name="age"
              className="form-control"
              value={user.age}
              onChange={Handleinput}
              />
        </div>
        <div className="form-group">
          <label>Location </label>
          <input 
              type="text" 
              name="location"
              className="form-control"
              value={user.location}
              onChange={Handleinput}
              />
        </div>
        <div className="form-group">
          <label>Date: </label>
          <div>
            <DatePicker
              selected={user.date}
              onChange={Handleinput}
            />
          </div>
        </div>

        <div className="form-group">
          <input type="submit" value="Create Exercise Log" className="btn btn-primary"  onClick={postdata}/>
        </div>
      </form>
    </Container>
    )
  }

  export default CreateData;
