import React, { Component  , useState} from 'react';
import axios from 'axios';
import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";
import {History, useHistory} from "react-router-dom";
import { Container } from 'react-bootstrap';

export default class CreateExercise extends Component {
  constructor(props) {
    super(props);

    this.onChangeUsername = this.onChangeUsername.bind(this);
    this.onChangeage= this.onChangeDescription.bind(this);
    this.onChangeloat = this.onChangeDuration.bind(this);
    this.onChangeDate = this.onChangeDate.bind(this);
    this.onSubmit = this.onSubmit.bind(this);

    this.state = {
      username: '',
      description: '',
      duration: 0,
      date: new Date(),
      users: []
    }
  }

  componentDidMount() {
    axios.get('http://localhost:5000/users/')
      .then(response => {
        if (response.data.length > 0) {
          this.setState({
            users: response.data.map(user => user.username),
            username: response.data[0].username
          })
        }
      })
      .catch((error) => {
        console.log(error);
      })

  }

  onChangeUsername(e) {
    this.setState({
      username: e.target.value
    })
  }

  onChangeDescription(e) {
    this.setState({
      description: e.target.value
    })
  }

  onChangeDuration(e) {
    this.setState({
      duration: e.target.value
    })
  }

  onChangeDate(date) {
    this.setState({
      date: date
    })
  }

  onSubmit(e) {
    e.preventDefault();

    const exercise = {
      username: this.state.username,
      description: this.state.description,
      duration: this.state.duration,
      date: this.state.date
    }

    console.log(exercise);

    axios.post('http://localhost:5000/exercises/add', exercise)
      .then(res => console.log(res.data));

    window.location = '/';
  }

  render() {

    return (
    <Container>
      <h3>Create New Exercise Log</h3>
      <form method="POST" encType="multipart/form-data">
        <div className="form-group"> 
          <label>Username: </label>
          <input  type="text"
              required
              name="username"
              className="form-control"
              value={user.username}
              onChange={Handleinput}
              />
        </div>
        <div className="form-group"> 
          <label>Image</label>
          <input  type="file" method="POST"
              required
              className="form-control"
             
              filename="img"
              value={user.img}
               onChange={fileinput}
              />
        </div>
        <div className="form-group"> 
          <label>Age </label>
          <input  type="text"
              required
              name="age"
              className="form-control"
              value={user.age}
              onChange={Handleinput}
              />
        </div>
        <div className="form-group">
          <label>Location </label>
          <input 
              type="text" 
              name="location"
              className="form-control"
              value={user.location}
              onChange={Handleinput}
              />
        </div>
        <div className="form-group">
          <label>Date: </label>
          <div>
           <input
             type="text" 
              name="date"
              className="form-control"
              value={user.date}
              onChange={Handleinput}
            />  
              
          </div>
        </div>

        <div className="form-group">
          <input type="submit" value="Create Exercise Log" className="btn btn-primary"  onClick={postdata}/>
        </div>
      </form>
    </Container>
    )
  }



}
